import React, { useState, useContext, useEffect } from "react";
import axios from "axios";

import GeneralContext from "./GeneralContext";

import { Tooltip, Grow } from "@mui/material";

import {
  BarChartOutlined,
  KeyboardArrowDown,
  KeyboardArrowUp,
  MoreHoriz,
} from "@mui/icons-material";

import { watchlist } from "../data/data";

const API_KEY = process.env.REACT_APP_API_SECRET_KEY;

const initialWatchlist = [
  { name: "INFY", symbol: "INFY.NS" },
  { name: "TCS", symbol: "TCS.NS" },
  { name: "WIPRO", symbol: "WIPRO.NS" },
  { name: "RELIANCE", symbol: "RELIANCE.NS" },
  { name: "HDFCBANK", symbol: "HDFCBANK.NS" },
  { name: "ONGC", symbol: "ONGC.NS" },
];

const WatchList = () => {
  const [watchlist, setWatchlist] = useState(initialWatchlist);
  const [stockData, setStockData] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [newSymbol, setNewSymbol] = useState("");
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchPrices = async () => {
      setLoading(true);
      try {
        const symbols = watchlist.map((s) => s.symbol).join(",");
        const url = `http://api.marketstack.com/v1/eod?access_key=${API_KEY}&symbols=${symbols}&limit=8&sort=desc`;
        const response = await axios.get(url);
        const data = response.data.data;
        // console.log(response.data.data);

        const fetchedData = data.map((stock) => {
          const change = stock.close - stock.open;
          const percent = (change / stock.open) * 100;
          const flag = percent > 0 ? "true" : "false";

          return {
            name: stock.symbol,
            price: stock.close,
            percent: percent.toFixed(2),
            isDown: flag === "true" ? false : true, // optional
          };
        });
        // console.log(fetchedData);

        const updatedStockData = watchlist.map((stock) => {
          const apiStock = data.find((item) => item.symbol === stock.symbol);
          if (!apiStock || apiStock.close === undefined) {
            return {
              ...stock,
              price: "-",
              percent: "-",
              isDown: false,
            };
          }

          const change = apiStock.close - apiStock.open;
          const percent = (change / apiStock.open) * 100;
          return {
            ...stock,
            price: apiStock.close.toFixed(2),
            percent: percent.toFixed(2) + "%",
            isDown: change < 0,
          };
        });

        setStockData(fetchedData);
      } catch (error) {
        console.error("Marketstack API error:", error);
      } finally {
        setLoading(false);
      }
    };

    fetchPrices();
  }, [watchlist]);
  
  

  return (
    <div className="watchlist-container">
      <div className="search-container">
        <input
          type="text"
          name="search"
          id="search"
          placeholder="Search eg:infy, bse, nifty fut weekly, gold mcx"
          className="search"
        />
        <span className="counts"> {stockData.length} / 50</span>
      </div>

      <ul className="list">
        {stockData.map((stock, index) => {
          return <WatchListItem stock={stock} key={index} />;
        })}
      </ul>
    </div>
  );
};

export default WatchList;

const WatchListItem = ({ stock }) => {
  const [showWatchlistActions, setShowWatchlistActions] = useState(false);

  const handleMouseEnter = (e) => {
    setShowWatchlistActions(true);
  };

  const handleMouseLeave = (e) => {
    setShowWatchlistActions(false);
  };

  return (
    <li onMouseEnter={handleMouseEnter} onMouseLeave={handleMouseLeave}>
      <div className="item">
        <p className={stock.isDown ? "down" : "up"}>{stock.name}</p>
        <div className="itemInfo">
          <span className="percent">{stock.percent}</span>
          {stock.isDown ? (
            <KeyboardArrowDown className="down" />
          ) : (
            <KeyboardArrowUp className="down" />
          )}
          <span className="price">{stock.price}</span>
        </div>
      </div>
      {showWatchlistActions && <WatchListActions uid={stock.name} />}
    </li>
  );
};

const WatchListActions = ({ uid }) => {
  const generalContext = useContext(GeneralContext);

  const handleBuyClick = () => {
    generalContext.openBuyWindow(uid);
  };

  const handleSellClick = () => {
    generalContext.openSellWindow(uid);
  };

  return (
    <span className="actions">
      <span>
        <Tooltip
          title="Buy (B)"
          placement="top"
          arrow
          TransitionComponent={Grow}
          onClick={handleBuyClick}
        >
          <button className="buy">Buy</button>
        </Tooltip>
        <Tooltip
          title="Sell (S)"
          placement="top"
          arrow
          TransitionComponent={Grow}
          onClick={handleSellClick}
        >
          <button className="sell">Sell</button>
        </Tooltip>
        <Tooltip
          title="Analytics (A)"
          placement="top"
          arrow
          TransitionComponent={Grow}
        >
          <button className="action">
            <BarChartOutlined className="icon" />
          </button>
        </Tooltip>
        <Tooltip title="More" placement="top" arrow TransitionComponent={Grow}>
          <button className="action">
            <MoreHoriz className="icon" />
          </button>
        </Tooltip>
      </span>
    </span>
  );
};
